/**
 * PDF.js Worker
 * 
 * This is a simplified worker file for PDF.js.
 * In production, you should replace this with the actual PDF.js worker from the package.
 */

// Simple polyfill for the PDF.js worker
self.onmessage = function(event) {
  // Log the received message for debugging
  console.log('Worker received message:', event.data);
  
  // Send a response to indicate the worker is active
  self.postMessage({
    type: 'workerInitialized',
    initialized: true
  });
};

// Mock PDF.js worker methods
self.pdfjsWorker = {
  isActive: true,
  version: '3.11.174'  // This should match the version in your react-pdf package
};

console.log('PDF.js Worker initialized');
